    "saturationFactor120QAxis" DOUBLE PRECISION NOT NULL,
    -- Quadrature-axis saturation factor at rated terminal voltage (<i>S1q</i>)
    -- (&gt;= 0). Typical value = 0,02.
    "saturationFactorQAxis" DOUBLE PRECISION NOT NULL
);

