query.value:
SELECT DISTINCT ?name ?id ?nomv WHERE {
 ?sys c:IdentifiedObject.mRID ?sysid.
 ?s c:ConnectivityNode.ConnectivityNodeContainer ?sys.
 ?s r:type c:ConnectivityNode.
 ?s c:IdentifiedObject.name ?name.
 ?s c:IdentifiedObject.mRID ?id.
 { 
   ?ce c:Equipment.EquipmentContainer ?sys.
   ?ce e:ConductingEquipment.FromConnectivityNode ?s.
   ?ce c:ConductingEquipment.BaseVoltage ?bv.
 } UNION { 
   ?ce c:Equipment.EquipmentContainer ?sys.
   ?ce e:ConductingEquipment.ToConnectivityNode ?s.
   ?ce c:ConductingEquipment.BaseVoltage ?bv.
 } UNION {
   ?pxf c:Equipment.EquipmentContainer ?sys.
   ?end c:PowerTransformerEnd.PowerTransformer ?pxf.
   ?end e:TransformerEnd.ConnectivityNode ?s.
   ?end c:TransformerEnd.BaseVoltage ?bv.
 }
 ?bv c:BaseVoltage.nominalVoltage ?nomv.
}
ORDER by ?name

SELECT ?name ?id WHERE {
 ?s r:type c:EquipmentContainer.
 ?s c:IdentifiedObject.name ?name.
 ?s c:IdentifiedObject.mRID ?id.
}
ORDER by ?name

SELECT DISTINCT ?vnom WHERE {
 ?sys c:IdentifiedObject.mRID ?sysid.
 ?s c:Equipment.EquipmentContainer ?sys.
 {?s c:ConductingEquipment.BaseVoltage ?lev.}
  UNION
 { ?end c:PowerTransformerEnd.PowerTransformer ?s.
   ?end c:TransformerEnd.BaseVoltage ?lev.}
 ?lev r:type c:BaseVoltage.
 ?lev c:BaseVoltage.nominalVoltage ?vstr.
  bind(xsd:string(?vstr) as ?xsdstr)
  bind(xsd:float(?xsdstr) as ?vnom)
}
ORDER BY ?vnom

SELECT DISTINCT ?cnid ?vmag ?vdeg WHERE {
 ?sv e:SvVoltage.ConnectivityNode ?cn.
  bind(substr(str(?cn),strlen(str(?cn))-35) as ?cnid)
 ?sv c:SvVoltage.v ?vmag.
 ?sv c:SvVoltage.angle ?vdeg
} ORDER BY ?cnid

SELECT DISTINCT ?eqid ?p ?q WHERE {
 ?sv e:SvPowerFlow.ConductingEquipment ?eq.
  bind(substr(str(?eq),strlen(str(?eq))-35) as ?eqid)
 ?sv c:SvPowerFlow.p ?p.
 ?sv c:SvPowerFlow.q ?q
} ORDER BY ?eqid

SELECT DISTINCT ?endid ?p ?q WHERE {
 ?sv e:SvPowerFlow.TransformerEnd ?end.
  bind(substr(str(?end),strlen(str(?end))-35) as ?endid)
 ?sv c:SvPowerFlow.p ?p.
 ?sv c:SvPowerFlow.q ?q
} ORDER BY ?endid
